// Simple ID generator if uuid isn't available in environment
const generateId = () => Math.random().toString(36).substr(2, 9);

const AdminPage: React.FC = () => {
  const { products, addProduct, updateProduct, deleteProduct, resetToDefault } = useProducts();
  const [isEditing, setIsEditing] = useState(false);
  const [showForm, setShowForm] = useState(false);

  const emptyProduct: Product = {
    id: '',
    name: '',
    category: 'Cake',
    sections: [],
    description: '',
    prices: { '0.5kg': 0, '1kg': 0 },
    images: [''],
    paymentTier: 'standard'
  };

  const [formData, setFormData] = useState<Product>(emptyProduct);

  // Helper to convert Google Drive links to direct view links
  const processImageLink = (url: string) => {
    // Regex to extract ID from Drive URLs
    const driveRegex = /\/d\/([^/]+)/;
    const match = url.match(driveRegex);
    if (match && match[1]) {
      return `https://drive.google.com/uc?export=view&id=${match[1]}`;
    }
    return url;
  };

  const handleEdit = (product: Product) => {
    setFormData(product);
    setIsEditing(true);
    setShowForm(true);
    window.scrollTo(0, 0);
  };

  const handleDelete = (id: string) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      deleteProduct(id);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Process images
    const processedImages = formData.images
      .filter(img => img.trim() !== '')
      .map(img => processImageLink(img));

    const productToSave = {
      ...formData,
      images: processedImages.length > 0 ? processedImages : ['https://via.placeholder.com/400?text=No+Image']
    };

    if (isEditing) {
      updateProduct(productToSave);
