import React, { useState, useMemo } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import ProductCard from '../components/ProductCard';
import { useProducts } from '../context/ProductContext';

interface HomeProps {
  wishlist: string[];
  onToggleWishlist: (id: string) => void;
}

// Simple fuzzy matching helper
const calculateRelevance = (query: string, text: string): number => {
  const q = query.toLowerCase();
  const t = text.toLowerCase();

  if (t === q) return 100;
  if (t.startsWith(q)) return 80;
  if (t.includes(q)) return 60;

  // Fuzzy check for typos (e.g. "button" -> "butterscotch")
  let qIdx = 0;
  let matches = 0;
  for (let i = 0; i < t.length && qIdx < q.length; i++) {
    if (t[i] === q[qIdx]) {
      matches++;
      qIdx++;
    }
  }

  const score = (matches / q.length) * 40;
  return score > 20 ? score : 0;
};

function Home({ wishlist, onToggleWishlist }: HomeProps) {
  const navigate = useNavigate();
  const { products, sections } = useProducts();

  const confectioneryProducts = products.filter(p => p.category === 'Confectionery');

  const [searchQuery, setSearchQuery] = useState('');
  const [isSearchFocused, setIsSearchFocused] = useState(false);
  const [showContributor, setShowContributor] = useState(false);

  // Search Logic
  const searchResults = useMemo(() => {
    if (!searchQuery.trim()) return [];

    // 1. Exact/Substring/Fuzzy matches
    let matches = products.map(p => ({
      product: p,
      score: calculateRelevance(searchQuery, p.name)
    })).filter(r => r.score > 0);

    // Sort by score
    matches.sort((a, b) => b.score - a.score);

    // If we have matches, return top 5
    if (matches.length > 0) {
      return matches.slice(0, 5).map(m => m.product);
    }

    // 2. Fallback to default popular cakes if no matches found
    const defaults = products.filter(p => 
      ['blackforest', 'vanilla', 'choco-truffle', 'pineapple'].includes(p.id)
    );
    return defaults;
  }, [searchQuery, products]);

  // Determine if we are showing fallback results
  const isFallback = searchQuery.trim().length > 0 && 
                     searchResults.length > 0 && 
                     !products.some(p => calculateRelevance(searchQuery, p.name) > 0);

  return (
    <div className="animate-fade-in-up">
      {/* Hero Header */}
      <section className="px-6 pt-12 pb-6 bg-gradient-to-b from-pink-50 to-transparent text-center">
        <div className="inline-block px-4 py-1.5 bg-white rounded-full shadow-sm text-pink-500 text-[10px] font-black uppercase tracking-[0.2em] mb-4 border border-pink-100">
          Premium Bakery
        </div>
        <h1 className="text-5xl font-black text-gray-900 mb-4 tracking-tight leading-none">Freshly Baked <br/><span className="text-pink-500">With Love</span></h1>
        <p className="text-gray-500 mb-8 max-w-xs mx-auto text-sm font-medium leading-relaxed">Artisan cakes and sweet treats handcrafted and delivered straight to your door.</p>

        {/* Category Shortcuts */}
        <div className="flex justify-center space-x-4 mb-8">
          <Link to="/category/Cake" className="flex-1 max-w-[140px] py-4 bg-white rounded-[1.5rem] shadow-sm font-black text-[11px] uppercase tracking-widest text-gray-800 border border-pink-100 hover:border-pink-300 active:scale-95 transition-all">
            Cakes
          </Link>
          <Link to="/category/Confectionery" className="flex-1 max-w-[140px] py-4 bg-white rounded-[1.5rem] shadow-sm font-black text-[11px] uppercase tracking-widest text-gray-800 border border-pink-100 hover:border-pink-300 active:scale-95 transition-all">
            Confectionery
          </Link>
        </div>

        {/* Search Bar */}
        <div className="relative max-w-md mx-auto z-30">
          <div className={`relative flex items-center bg-white rounded-2xl shadow-sm border transition-all duration-300 ${isSearchFocused ? 'border-pink-400 ring-4 ring-pink-50 shadow-lg' : 'border-pink-100'}`}>
            <i className="fas fa-search text-gray-400 ml-4 text-lg"></i>
            <input 
              type="text" 
              placeholder="Search cakes..." 
              className="w-full py-4 px-3 bg-transparent outline-none text-gray-700 font-medium placeholder:text-gray-300"
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              onFocus={() => setIsSearchFocused(true)}
              onBlur={() => setTimeout(() => setIsSearchFocused(false), 200)}
            />
            {searchQuery && (
              <button 
                onClick={() => setSearchQuery('')}
                className="mr-4 text-gray-300 hover:text-pink-500"
              >
                <i className="fas fa-times-circle"></i>
              </button>
            )}
          </div>

          {/* Search Results Dropdown */}
          {searchQuery && (isSearchFocused || searchResults.length > 0) && (
            <div className="absolute top-full left-0 right-0 mt-2 bg-white rounded-2xl shadow-xl border border-pink-50 overflow-hidden animate-in slide-in-from-top-2 duration-200 z-50">
              {isFallback && (
                 <div className="px-4 py-3 bg-pink-50/50 text-[10px] font-bold text-pink-500 uppercase tracking-widest border-b border-pink-100">
                   Not found. Try these popular cakes:
                 </div>
              )}
              {searchResults.map((product) => (
                <div 
                  key={product.id}
                  onClick={() => navigate(`/product/${product.id}`)}
                  className="flex items-center p-3 hover:bg-pink-50 cursor-pointer border-b border-gray-50 last:border-0 transition-colors group"
                >
                  <img src={product.images[0]} alt={product.name} className="w-12 h-12 rounded-lg object-cover border border-pink-50" />
                  <div className="ml-3 text-left flex-grow">
                    <h4 className="font-bold text-gray-800 text-sm group-hover:text-pink-600 transition-colors font-serif">{product.name}</h4>
                    <span className="text-xs text-gray-400">{product.category}</span>
                  </div>
                  <div className="text-pink-500">
                     <i className="fas fa-chevron-right text-xs opacity-0 group-hover:opacity-100 transition-opacity transform group-hover:translate-x-1"></i>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </section>

      {/* Featured Sections - Dynamically Loaded from Context */}
      {sections.map((sectionName) => {
        const sectionProducts = products.filter(p => p.category === 'Cake' && p.sections.includes(sectionName));
        if (sectionProducts.length === 0) return null;

        return (
          <section key={sectionName} className="py-8">
            <div className="px-6 mb-6 flex justify-between items-end">
              <div>
                <span className="text-pink-500 font-black text-[10px] tracking-widest uppercase mb-1 block">Collection</span>
                <h2 className="text-3xl font-black text-gray-900 font-serif tracking-tight">{sectionName}</h2>
              </div>
              <Link to={`/category/Cake`} className="w-10 h-10 rounded-full bg-pink-50 flex items-center justify-center text-pink-500 shadow-sm active:scale-90 transition-transform">
                <i className="fas fa-arrow-right text-xs"></i>
              </Link>
            </div>

            <div className="flex overflow-x-auto gap-5 px-6 pb-6 hide-scrollbar">
              {sectionProducts.map((product) => (
                <div key={product.id} className="min-w-[220px] w-[220px]">
                  <ProductCard 
                    product={product} 
                    isWishlisted={wishlist.includes(product.id)}
                    onToggleWishlist={onToggleWishlist}
                  />
                </div>
              ))}
            </div>
          </section>
        );
      })}

      {/* Confectionery Section */}
      {confectioneryProducts.length > 0 && (
        <section className="py-10 bg-cream/30">
          <div className="px-6 mb-6 flex justify-between items-end">
            <div>
              <span className="text-pink-500 font-black text-[10px] tracking-widest uppercase mb-1 block">Quick Treats</span>
              <h2 className="text-3xl font-black text-gray-900 font-serif tracking-tight">Confectionery</h2>
            </div>
            <Link to={`/category/Confectionery`} className="w-10 h-10 rounded-full bg-white flex items-center justify-center text-pink-500 shadow-sm active:scale-90 transition-transform border border-pink-50">
              <i className="fas fa-arrow-right text-xs"></i>
            </Link>
          </div>
          <div className="flex overflow-x-auto gap-5 px-6 pb-10 hide-scrollbar">
            {confectioneryProducts.map((product) => (
              <div key={product.id} className="min-w-[220px] w-[220px]">
                <ProductCard 
                  product={product} 
                  isWishlisted={wishlist.includes(product.id)}
                  onToggleWishlist={onToggleWishlist}
                />
              </div>
            ))}
          </div>
        </section>
      )}

      {/* Trust Badges */}
      <section className="px-6 py-12 grid grid-cols-2 gap-4">
        <div className="p-6 bg-white rounded-3xl border border-pink-50 text-center">
          <i className="fas fa-truck text-pink-300 text-2xl mb-3"></i>
          <h4 className="text-[10px] font-black uppercase tracking-widest text-gray-800">Quick Delivery</h4>
        </div>
        <div className="p-6 bg-white rounded-3xl border border-pink-50 text-center">
          <i className="fas fa-shield-heart text-pink-300 text-2xl mb-3"></i>
          <h4 className="text-[10px] font-black uppercase tracking-widest text-gray-800">Hygienic Prep</h4>
        </div>
      </section>

      {/* Brand Ethos */}
      <section className="px-6 pt-16 pb-10 text-center bg-white mt-8 rounded-t-[3rem] shadow-[0_-10px_40px_rgba(0,0,0,0.02)]">
        <div className="w-20 h-20 bg-pink-50 rounded-full flex items-center justify-center mx-auto mb-6 shadow-inner border border-pink-100">
          <i className="fas fa-birthday-cake text-pink-400 text-3xl"></i>
        </div>
        <h3 className="text-2xl font-black mb-3 tracking-tight font-serif text-gray-900">Baked to Perfection</h3>
        <p className="text-gray-400 max-w-xs mx-auto text-sm font-medium leading-relaxed mb-10">
          Every order is baked fresh daily using premium ingredients because you deserve nothing but the best.
        </p>

        {/* Footer with Admin Login & Contributor */}
        <footer className="border-t border-gray-100 pt-8 pb-12">
          {/* Admin and Developer Links First */}
          <div className="flex justify-center items-center space-x-6 mb-6">
             <Link to="/admin" className="text-gray-400 text-[10px] hover:text-pink-500 font-bold uppercase tracking-widest transition-colors">
               Admin Login
             </Link>
             <span className="text-gray-200">|</span>
             <button 
               onClick={() => setShowContributor(true)}
               className="text-gray-400 text-[10px] hover:text-pink-500 font-bold uppercase tracking-widest transition-colors"
             >
               Developer - Jagdish
             </button>
          </div>

          {/* Copyright Last */}
          <p className="text-gray-300 text-[10px] font-medium tracking-wide">Â© 2024 Cakes N Bells</p>
        </footer>
      </section>

      {/* Contributor Popup */}
      {showContributor && (
        <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/50 backdrop-blur-sm animate-fade-in-up" onClick={() => setShowContributor(false)}>
          <div className="bg-white p-6 rounded-2xl shadow-2xl max-w-xs w-full text-center m-4" onClick={e => e.stopPropagation()}>
             <div className="w-16 h-16 bg-pink-100 rounded-full flex items-center justify-center mx-auto mb-4">
               <i className="fas fa-code text-pink-500 text-2xl"></i>
             </div>
             <h3 className="text-xl font-bold text-gray-800 font-serif mb-2">Project Contributor</h3>
             <p className="text-gray-600 font-bold text-lg mb-1">Jagdish</p>
             <p className="text-gray-400 text-sm mb-6">jagadish.omm@gmail.com</p>
             <button 
               onClick={() => setShowContributor(false)}
               className="px-6 py-2 bg-pink-500 text-white rounded-full font-bold shadow-lg shadow-pink-200"
             >
               Close
             </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Home;

import React, { useState } from 'react';
import { useProducts } from '../context/ProductContext';
import { Product, PaymentTier, Category, Section } from '../types';
import { Link } from 'react-router-dom';

// Simple ID generator if uuid isn't available in environment
const generateId = () => Math.random().toString(36).substr(2, 9);

const AdminPage: React.FC = () => {
  const { products, sections, addProduct, updateProduct, deleteProduct, addSection, deleteSection } = useProducts();
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [passwordInput, setPasswordInput] = useState('');
  const [loginError, setLoginError] = useState('');

  const [isEditing, setIsEditing] = useState(false);
  const [showForm, setShowForm] = useState(false);

  // State for new section input
  const [newSectionName, setNewSectionName] = useState('');

  const emptyProduct: Product = {
    id: '',
    name: '',
    category: 'Cake',
    sections: [],
    description: '',
    prices: { '0.5kg': 0, '1kg': 0 },
    images: [''],
    paymentTier: 'standard'
  };

  const [formData, setFormData] = useState<Product>(emptyProduct);

  // --- AUTHENTICATION LOGIC (HASHING) ---
  const handleLogin = async (e: React.FormEvent) => {
    e.preventDefault();

    // Convert input to SHA-256 Hash
    const msgBuffer = new TextEncoder().encode(passwordInput);
    const hashBuffer = await crypto.subtle.digest('SHA-256', msgBuffer);
    const hashArray = Array.from(new Uint8Array(hashBuffer));
    const hashHex = hashArray.map(b => b.toString(16).padStart(2, '0')).join('');

    // SHA-256 Hash of 'admin123'
    // This hides the actual password from plain view in the source code
    const CORRECT_HASH = '240be518fabd2724ddb6f04eeb1da5967448d7e831c08c8fa822809f74c720a9';

    if (hashHex === CORRECT_HASH) {
      setIsAuthenticated(true);
      setLoginError('');
    } else {
      setLoginError('Incorrect password');
    }
  };

  // --- IMAGE LOGIC ---
  const processImageLink = (url: string) => {
    // Regex for standard drive.google.com/file/d/... links
    const driveRegex = /\/d\/([^/]+)/;
    const match = url.match(driveRegex);

    // Regex for drive.google.com/open?id=... links
    const idRegex = /id=([^&]+)/;
    const matchId = url.match(idRegex);

    let fileId = '';
    if (match && match[1]) {
      fileId = match[1];
    } else if (matchId && matchId[1]) {
      fileId = matchId[1];
    }

    if (fileId) {
      // Using thumbnail API is much more reliable for <img> tags than uc?export=view
      // sz=w1000 requests a width of 1000px, which is good quality
      return `https://drive.google.com/thumbnail?id=${fileId}&sz=w1000`;
    }
    return url;
  };

  const handleAddImageField = () => {
    setFormData(prev => ({ ...prev, images: [...prev.images, ''] }));
  };

  const handleImageChange = (index: number, val: string) => {
    const newImages = [...formData.images];
    // Auto-convert link on paste
    newImages[index] = processImageLink(val);
    setFormData(prev => ({ ...prev, images: newImages }));
  };

  const handleRemoveImageField = (index: number) => {
    const newImages = formData.images.filter((_, i) => i !== index);
    setFormData(prev => ({ ...prev, images: newImages.length ? newImages : [''] }));
  };

  // --- SECTION MANAGEMENT LOGIC ---
  const handleAddSection = () => {
    if (newSectionName.trim()) {
      addSection(newSectionName.trim());
      setNewSectionName('');
    }
  };

  const handleDeleteSection = (sectionName: string) => {
    if (window.confirm(`Delete collection "${sectionName}"? Products tagged with this will no longer appear in this collection.`)) {
      deleteSection(sectionName);
    }
  };

  // --- CRUD LOGIC ---
  const handleEdit = (product: Product) => {
    setFormData(product);
    setIsEditing(true);
    setShowForm(true);
    window.scrollTo(0, 0);
  };

  const handleDelete = (id: string) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      deleteProduct(id);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Images are already processed in handleImageChange, but double check empty strings
    const processedImages = formData.images
      .filter(img => img.trim() !== '')

    const productToSave = {
      ...formData,
      images: processedImages.length > 0 ? processedImages : ['https://placehold.co/600x600?text=No+Image']
    };

    if (isEditing) {
      updateProduct(productToSave);
    } else {
      addProduct({ ...productToSave, id: generateId() });
    }

    setShowForm(false);
    setIsEditing(false);
    setFormData(emptyProduct);
  };

  const toggleProductSection = (section: string) => {
    if (formData.sections.includes(section)) {
      setFormData({ ...formData, sections: formData.sections.filter(s => s !== section) });
    } else {
      setFormData({ ...formData, sections: [...formData.sections, section] });
    }
  };

  // --- RENDER LOGIN SCREEN IF NOT AUTHENTICATED ---
  if (!isAuthenticated) {
    return (
      <div className="min-h-screen flex items-center justify-center p-6 animate-pop-in">
        <div className="bg-white p-8 rounded-3xl shadow-2xl border border-pink-100 max-w-sm w-full text-center">
          <div className="w-16 h-16 bg-pink-50 rounded-full flex items-center justify-center mx-auto mb-6">
            <i className="fas fa-lock text-pink-500 text-2xl"></i>
          </div>
          <h2 className="text-2xl font-black text-gray-800 mb-2 font-serif">Admin Access</h2>
          <p className="text-gray-400 text-sm mb-6">Enter password to manage store</p>

          <form onSubmit={handleLogin}>
            <input 
              type="password" 
              placeholder="Enter Password"
              className="w-full p-4 bg-gray-50 rounded-xl border border-gray-200 outline-none focus:border-pink-500 focus:bg-white transition-all text-center mb-4"
              value={passwordInput}
              onChange={(e) => setPasswordInput(e.target.value)}
              autoFocus
            />
            {loginError && <p className="text-red-500 text-xs font-bold mb-4">{loginError}</p>}

            <button 
              type="submit" 
              className="w-full py-4 bg-gray-900 text-white rounded-xl font-bold shadow-lg active:scale-95 transition-all"
            >
              Unlock Dashboard
            </button>
          </form>

          <Link to="/" className="block mt-6 text-gray-400 text-xs hover:text-pink-500">
            <i className="fas fa-arrow-left mr-1"></i> Back to Shop
          </Link>
        </div>
      </div>
    );
  }

  // --- RENDER DASHBOARD IF AUTHENTICATED ---
  return (
    <div className="p-4 max-w-4xl mx-auto animate-fade-in-up pb-20">
      <div className="flex justify-between items-center mb-8 mt-4">
        <div>
          <h1 className="text-3xl font-bold text-gray-800 font-serif">Store Admin</h1>
          <p className="text-gray-500 text-sm">Manage your products inventory</p>
        </div>
        <div className="flex space-x-2">
            <button 
                onClick={() => {
                setFormData(emptyProduct);
                setIsEditing(false);
                setShowForm(!showForm);
                }} 
                className="bg-pink-500 text-white px-4 py-2 rounded-xl font-bold shadow-lg shadow-pink-200 active:scale-95 transition-all"
            >
                {showForm ? 'Cancel' : '+ Add Product'}
            </button>
        </div>
      </div>

      {/* --- MANAGE SECTIONS PANEL --- */}
      {!showForm && (
        <div className="bg-white p-6 rounded-2xl shadow-sm border border-pink-50 mb-8">
          <h3 className="text-lg font-bold text-gray-800 mb-4 font-serif">Manage Collections</h3>
          <p className="text-xs text-gray-500 mb-4">Create new categories (e.g., "Wedding Cakes") to display on the Home page.</p>

          <div className="flex flex-wrap gap-2 mb-4">
            {sections.map(section => (
              <div key={section} className="flex items-center bg-gray-50 border border-gray-200 rounded-full pl-3 pr-1 py-1">
                <span className="text-xs font-bold text-gray-600 mr-2">{section}</span>
                <button 
                  onClick={() => handleDeleteSection(section)}
                  className="w-5 h-5 rounded-full bg-red-100 text-red-500 flex items-center justify-center hover:bg-red-200"
                >
                  <i className="fas fa-times text-[10px]"></i>
                </button>
              </div>
            ))}
          </div>

          <div className="flex gap-2">
            <input 
              type="text" 
              placeholder="New Collection Name"
              value={newSectionName}
              onChange={e => setNewSectionName(e.target.value)}
              className="flex-grow p-2 bg-gray-50 rounded-lg border border-gray-200 text-sm outline-none focus:border-pink-400"
            />
            <button 
              onClick={handleAddSection}
              disabled={!newSectionName.trim()}
              className="px-4 py-2 bg-gray-900 text-white rounded-lg text-xs font-bold disabled:opacity-50"
            >
              Add Collection
            </button>
          </div>
        </div>
      )}

      {showForm && (
        <div className="bg-white p-6 rounded-2xl shadow-xl border border-pink-100 mb-10 animate-pop-in">
          <h2 className="text-xl font-bold text-gray-800 mb-6 font-serif">
            {isEditing ? 'Edit Product' : 'Add New Product'}
          </h2>
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <label className="block text-xs font-bold text-gray-400 uppercase tracking-wider mb-2">Product Name</label>
                <input 
                  required
                  type="text" 
                  value={formData.name}
                  onChange={e => setFormData({...formData, name: e.target.value})}
                  className="w-full p-3 bg-gray-50 rounded-xl border border-gray-200 focus:border-pink-500 outline-none"
                />
              </div>

              <div>
                <label className="block text-xs font-bold text-gray-400 uppercase tracking-wider mb-2">Category</label>
                <select 
                  value={formData.category}
                  onChange={e => setFormData({...formData, category: e.target.value as Category})}
                  className="w-full p-3 bg-gray-50 rounded-xl border border-gray-200 focus:border-pink-500 outline-none"
                >
                  <option value="Cake">Cake</option>
                  <option value="Confectionery">Confectionery</option>
                </select>
              </div>
            </div>

            <div>
              <label className="block text-xs font-bold text-gray-400 uppercase tracking-wider mb-2">Description</label>
              <textarea 
                required
                rows={3}
                value={formData.description}
                onChange={e => setFormData({...formData, description: e.target.value})}
                className="w-full p-3 bg-gray-50 rounded-xl border border-gray-200 focus:border-pink-500 outline-none resize-none"
              />
            </div>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {formData.category === 'Cake' ? (
                <>
                  <div>
                    <label className="block text-xs font-bold text-gray-400 uppercase tracking-wider mb-2">Price (0.5kg)</label>
                    <input 
                      type="number" 
                      value={formData.prices['0.5kg'] || ''}
                      onChange={e => setFormData({...formData, prices: {...formData.prices, '0.5kg': Number(e.target.value)}})}
                      className="w-full p-3 bg-gray-50 rounded-xl border border-gray-200 focus:border-pink-500 outline-none"
                      placeholder="â‚¹"
                    />
                  </div>
                  <div>
                    <label className="block text-xs font-bold text-gray-400 uppercase tracking-wider mb-2">Price (1kg)</label>
                    <input 
                      type="number" 
                      value={formData.prices['1kg'] || ''}
                      onChange={e => setFormData({...formData, prices: {...formData.prices, '1kg': Number(e.target.value)}})}
                      className="w-full p-3 bg-gray-50 rounded-xl border border-gray-200 focus:border-pink-500 outline-none"
                      placeholder="â‚¹"
                    />
                  </div>
                </>
              ) : (
                 <div>
                    <label className="block text-xs font-bold text-gray-400 uppercase tracking-wider mb-2">Price (Piece)</label>
                    <input 
                      type="number" 
                      value={formData.prices['piece'] || ''}
                      onChange={e => setFormData({...formData, prices: {...formData.prices, 'piece': Number(e.target.value)}})}
                      className="w-full p-3 bg-gray-50 rounded-xl border border-gray-200 focus:border-pink-500 outline-none"
                      placeholder="â‚¹"
                    />
                  </div>
              )}

              <div>
                <label className="block text-xs font-bold text-gray-400 uppercase tracking-wider mb-2">Payment Tier</label>
                <select 
                  value={formData.paymentTier}
                  onChange={e => setFormData({...formData, paymentTier: e.target.value as PaymentTier})}
                  className="w-full p-3 bg-gray-50 rounded-xl border border-gray-200 focus:border-pink-500 outline-none"
                >
                  <option value="standard">Standard</option>
                  <option value="premium">Premium</option>
                  <option value="luxury">Luxury</option>
                </select>
              </div>
            </div>

            <div>
              <label className="block text-xs font-bold text-gray-400 uppercase tracking-wider mb-2">Sections (Collections)</label>
              <div className="flex flex-wrap gap-2">
                {sections.map(section => (
                  <button
                    type="button"
                    key={section}
                    onClick={() => toggleProductSection(section)}
                    className={`px-3 py-1.5 rounded-full text-xs font-bold border transition-all ${
                      formData.sections.includes(section)
                        ? 'bg-pink-500 text-white border-pink-500'
                        : 'bg-white text-gray-500 border-gray-200'
                    }`}
                  >
                    {section}
                  </button>
                ))}
              </div>
              <p className="text-[10px] text-gray-400 mt-2">Manage these tags in the "Manage Collections" panel above.</p>
            </div>

            <div>
              <label className="block text-xs font-bold text-gray-400 uppercase tracking-wider mb-2">Product Images</label>
              <div className="bg-blue-50 p-3 rounded-lg mb-4 text-xs text-blue-800 border border-blue-100">
                <strong>Tip:</strong> Paste a Google Drive share link, and we'll automatically convert it for the app.
                Ensure the file in Drive is set to <strong>"Anyone with the link can view"</strong>.
              </div>

              {formData.images.map((img, idx) => (
                <div key={idx} className="mb-4">
                  <div className="flex gap-2 mb-2">
                    <input 
                      type="text" 
                      value={img}
                      onChange={e => handleImageChange(idx, e.target.value)}
                      className="flex-grow p-3 bg-gray-50 rounded-xl border border-gray-200 focus:border-pink-500 outline-none"
                      placeholder="Image URL (Paste Drive Link Here)"
                    />
                    <button 
                      type="button" 
                      onClick={() => handleRemoveImageField(idx)}
                      className="p-3 text-red-400 hover:text-red-600 font-bold"
                    >
                      <i className="fas fa-trash"></i>
                    </button>
                  </div>
                  {/* Image Preview for Admin */}
                  {img && (
                    <div className="w-20 h-20 rounded-lg overflow-hidden border border-gray-200 bg-gray-100">
                      <img 
                        src={img} 
                        alt="Preview" 
                        className="w-full h-full object-cover"
                        referrerPolicy="no-referrer"
                        onError={(e) => e.currentTarget.src = 'https://placehold.co/100x100?text=Error'}
                      />
                    </div>
                  )}
                </div>
              ))}
              <button 
                type="button" 
                onClick={handleAddImageField}
                className="text-pink-500 text-xs font-bold uppercase tracking-wider hover:text-pink-600"
              >
                + Add Another Image
              </button>
            </div>

            <button type="submit" className="w-full py-4 bg-gray-900 text-white rounded-xl font-bold shadow-lg active:scale-[0.98] transition-all">
              {isEditing ? 'Save Changes' : 'Create Product'}
            </button>
          </form>
        </div>
      )}

      <div className="grid grid-cols-1 gap-4">
        {products.map(product => (
          <div key={product.id} className="bg-white p-4 rounded-xl border border-pink-50 flex items-center space-x-4 shadow-sm hover:shadow-md transition-shadow">
            <img 
                src={product.images[0]} 
                alt={product.name} 
                className="w-16 h-16 rounded-lg object-cover bg-gray-100" 
                referrerPolicy="no-referrer"
                onError={(e) => e.currentTarget.src = 'https://placehold.co/100x100?text=No+Img'}
            />
            <div className="flex-grow">
              <h3 className="font-bold text-gray-800">{product.name}</h3>
              <p className="text-xs text-gray-500">{product.category} â€¢ {product.paymentTier}</p>
            </div>
            <div className="flex space-x-2">
              <button 
                onClick={() => handleEdit(product)}
                className="w-8 h-8 rounded-full bg-blue-50 text-blue-500 flex items-center justify-center hover:bg-blue-100"
              >
                <i className="fas fa-edit text-xs"></i>
              </button>
              <button 
                onClick={() => handleDelete(product.id)}
                className="w-8 h-8 rounded-full bg-red-50 text-red-500 flex items-center justify-center hover:bg-red-100"
              >
                <i className="fas fa-trash text-xs"></i>
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
